import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = ({ role }) => {
    const location = useLocation();

    const adminLinks = [
        { path: '/admin/dashboard', label: 'Dashboard', icon: 'üìä' },
        { path: '/admin/employees', label: 'Employees', icon: 'üë•' },
        { path: '/admin/tasks', label: 'Task Management', icon: 'üìã' },
        { path: '/admin/shifts', label: 'Shift Management', icon: '‚è∞' },
        { path: '/admin/reports', label: 'Reports', icon: 'üìù' },
    ];

    const employeeLinks = [
        { path: '/employee/dashboard', label: 'Dashboard', icon: 'üìä' },
        { path: '/employee/tasks', label: 'My Tasks', icon: 'üìã' },
        { path: '/employee/shift', label: 'My Shift', icon: '‚è∞' },
        { path: '/employee/reports', label: 'Submit Report', icon: 'üìù' },
        { path: '/employee/history', label: 'Report History', icon: 'üìö' },
    ];

    const links = role === 'admin' ? adminLinks : employeeLinks;

    return (
        <aside className="sidebar">
            <div className="sidebar-content">
                {links.map((link) => (
                    <Link
                        key={link.path}
                        to={link.path}
                        className={`sidebar-link ${location.pathname === link.path ? 'active' : ''}`}
                    >
                        <span className="sidebar-icon">{link.icon}</span>
                        <span className="sidebar-label">{link.label}</span>
                    </Link>
                ))}
            </div>
        </aside>
    );
};

export default Sidebar;
